 modifier = Modifier.padding(start = 8.dp)
 ) {
 Text(
 text = "Hi Ujjwal",
 fontSize = 20.sp,
 fontWeight = FontWeight.Bold
 )
 Text(
 text = "Order and Eat",
 fontSize = 20.sp,
 fontWeight = FontWeight.Bold
 )
 }

 // Right side - Profile image
 Image(
 painter = painterResource(id = R.drawable.human), 
 contentDescription = "Profile Picture",
 modifier = Modifier
 .size(70.dp)
 .padding(4.dp)
 )
 }
}

